INSERT INTO product (sku, name, description, image_url, active, units_in_stock, unit_price, category_id, date_created) VALUES ('QTKRE016', 'tokyo revengers n.16', '¡EL volumen 16 de la historia de suspenso con saltos en el tiempo! Después de conseguir información sobre la pandilla de Yokohama conocida como Tenjiku, y de averiguar más sobre su líder, Izana Kurokawa, Takemichi regresa del pasado. Pero ahora ya no hay manera de regresar al futuro.¡Por fin comienza la última batalla de Takemichi!', 'assets/images/products/tokyo_revengers/tokyo_revengers16.webp', 1, 100, 7000, 12, NOW());